mapper.select:
SELECT COUNT(1) FROM categories

    AND category_name = #{name}


            AND parent_id IS NULL


            AND parent_id = #{parentId}



        AND category_id <> #{excludeId}

SELECT COUNT(1) FROM categories

     AND parent_id = #{parentId} 
     AND category_level = #{level} 
     AND category_name LIKE CONCAT('%',#{q},'%')

SELECT category_id, parent_id, category_name, category_level, created_at FROM categories

     AND parent_id = #{parentId} 
     AND category_level = #{level} 
     AND category_name LIKE CONCAT('%',#{q},'%') 


    ${orderSql}


    LIMIT #{limit}

        OFFSET #{offset}

mapper.update:
UPDATE categories
SET parent_id = #{parentId}, category_name = #{name}, category_level = #{level}
WHERE category_id = #{id}
