mapper.select:
SELECT media_id, product_id, media_type, media_url, file_name, file_size, mime_type, display_order, is_embedded, created_at
FROM media_resources
WHERE media_id = #{mediaId}

mapper.update:
UPDATE media_resources
media_type = #{mediaType}, media_url = #{mediaUrl}, file_name = #{fileName}, file_size = #{fileSize}, mime_type = #{mimeType}, display_order = #{displayOrder}, is_embedded = #{isEmbedded},
WHERE media_id = #{mediaId}
