mapper.select:
SELECT product_id, seller_id, category_id, product_name, product_desc, price, stock_quantity, search_keywords, product_status, created_at, updated_at
FROM products
WHERE product_id = #{productId}

SELECT p.*,
(SELECT pi.image_url
FROM product_images pi
WHERE pi.product_id = p.product_id
ORDER BY pi.image_order ASC, pi.image_id ASC
LIMIT 1) AS cover_image
FROM products p
WHERE 1 = 1

    AND (product_name LIKE CONCAT('%', #{q}, '%') OR search_keywords LIKE CONCAT('%', #{q}, '%'))


    AND category_id = #{categoryId}


    AND product_status = #{status}




        ORDER BY price ${order}


        ORDER BY stock_quantity ${order}


        ORDER BY created_at ${order}


        ORDER BY created_at ${order}



LIMIT #{offset}, #{size}

SELECT COUNT(1)
FROM products
WHERE 1 = 1

    AND (product_name LIKE CONCAT('%', #{q}, '%') OR search_keywords LIKE CONCAT('%', #{q}, '%'))


    AND category_id = #{categoryId}


    AND product_status = #{status}

SELECT product_id, seller_id, category_id, product_name, product_desc, price, stock_quantity, search_keywords, product_status, created_at, updated_at
FROM products
WHERE product_id = #{productId}

SELECT category_id, parent_id, category_name, category_level, created_at
FROM categories
WHERE category_id = #{categoryId}

mapper.update:
UPDATE products
category_id = #{categoryId}, product_name = #{productName}, product_desc = #{productDesc}, price = #{price}, stock_quantity = #{stockQuantity}, search_keywords = #{searchKeywords}, product_status = #{productStatus}, updated_at = #{updatedAt},
WHERE product_id = #{productId}

UPDATE products
SET stock_quantity = stock_quantity - #{deduct},
product_status = CASE
WHEN stock_quantity - #{deduct} <= 0 THEN 'outOfStock'
ELSE product_status
END,
updated_at = #{now}
WHERE product_id = #{productId}
AND stock_quantity >= #{deduct}
